<script>
 export default {
   name: "Footer",

   data() {
     return {
       info: [
         { text: 'footer.info.interior' },
         { text: 'footer.info.architecture' },
         { text: 'footer.info.turnkey' },
       ],
       isActive: false,
     }
   },

   methods:{
     openForm(){
       document.body.classList.add("formIsOpened");
     }
   }
 }

</script>

<style lang="scss">
.footer {
  margin-top: 100px;
  @media (min-width: 768px) {
    margin-top: 170px;
  }
  &__main{
    padding-top: 75px;
    padding-bottom: 120px;
    border-top: 1px solid #5d5c5b;
    @media (max-width: 768px){
      padding-top: 50px;
      padding-bottom: 30px;
    }
  }
  .row {
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
  }
  .coll:nth-child(1){
    min-width: 320px;
    @media (max-width: 768px){
      width: 100%;
      display: flex;
      flex-direction: column;
      align-items: center;
    }
  }
  .coll:nth-child(2){
    padding: 20px;
    @media (max-width: 1200px){
      padding: 0;
      display: flex;
      flex-direction: column;
      justify-content: space-between;
    }
    @media (max-width: 768px){
      width: 100%;
      text-align: center;
      margin-top: 40px;
    }

  }
  .coll:nth-child(3){
    @media (max-width: 1200px) {
      width: 100%;
      display: flex;
      justify-content: center;
      margin-top: 40px;
    }
    @media (max-width: 576px){
      flex-direction: column-reverse;
    }
  }
  &__logo{
    margin-bottom: 35px;
  }
  &__list{
    padding-left: 20px;
    font-size: 16px;
    line-height: 1.3;
    font-family: "Gotham Pro Light", sans-serif;

  }
  &__soc{
    margin-bottom: 30px;
    text-align: center;
  }
  &__consultation{
    padding: 20px 30px;
    border: 1px solid #ffffff;
    background-color: transparent;
    outline: none;
    cursor: pointer;
    color: #ffffff;
    text-transform: uppercase;
    font-size: 20px;
    transition: all 0.3s;
    max-width: 450px;
    margin: 0 auto;
    &:hover,
    &:focus{
      color: #888888;
      border-color: #888888;
    }
    @media (max-width: 768px){
      font-size: 15px;
    }
  }
  &__langs-phone{
    display: flex;
    align-items: center;
  }
  &__langs{
    padding-right: 25px;
    margin-right: 25px;
    position: relative;
    &::after{
      content: '|';
      position: absolute;
      right: 0;
      top: 0;
    }
    @media (max-width: 576px){
      padding: 0;
      margin: 0;
      margin-top: 20px;
      &:after{
        display: none;
      }
    }
  }
}
</style>

